import React, { useState } from 'react';
import { useFormik } from 'formik';
import * as Yup from 'yup'; // Import Yup for validation
import '../App.css';

const JobApplicationForm = () => {
  const [position, setPosition] = useState('');

  const formik = useFormik({
    initialValues: {
      fullName: '',
      email: '',
      phoneNumber: '',
      applyingFor: '',
      relevantExperience: '',
      portfolioURL: '',
      managementExperience: '',
      additionalSkills: [],
      preferredInterviewTime: '',
    },
    validationSchema: Yup.object().shape({
      fullName: Yup.string().required('Full Name is required'),
      email: Yup.string().email('Invalid email address').required('Email is required'),
      phoneNumber: Yup.string().required('Phone Number is required').matches(/^[0-9]+$/, 'Must be a valid number'),
      applyingFor: Yup.string().required('Applying for Position is required'),
      relevantExperience: Yup.number().when('applyingFor', {
        is: val => val === 'Developer' || val === 'Designer',
        then: Yup.number().positive('Must be greater than 0').required('Relevant Experience is required')
      }),
      portfolioURL: Yup.string().when('applyingFor', {
        is: 'Designer',
        then: Yup.string().url('Invalid URL').required('Portfolio URL is required')
      }),
      managementExperience: Yup.string().when('applyingFor', {
        is: 'Manager',
        then: Yup.string().required('Management Experience is required')
      }),
      additionalSkills: Yup.array().min(1, 'At least one skill must be selected'),
      preferredInterviewTime: Yup.date().required('Preferred Interview Time is required').nullable(),
    }),
    onSubmit: values => {
      alert(JSON.stringify(values, null, 2));
    },
  });

  const handleApplyingForChange = (e) => {
    formik.handleChange(e);
    setPosition(e.target.value);
  };

  return (
    <form onSubmit={formik.handleSubmit} className="job-application-form">
      <div className="form-control">
        <label htmlFor="fullName">Full Name:</label>
        <input
          id="fullName"
          name="fullName"
          type="text"
          onChange={formik.handleChange}
          onBlur={formik.handleBlur}
          value={formik.values.fullName}
        />
        {formik.touched.fullName && formik.errors.fullName ? (
          <div className="error">{formik.errors.fullName}</div>
        ) : null}
      </div>

      <div className="form-control">
        <label htmlFor="email">Email:</label>
        <input
          id="email"
          name="email"
          type="email"
          onChange={formik.handleChange}
          onBlur={formik.handleBlur}
          value={formik.values.email}
        />
        {formik.touched.email && formik.errors.email ? (
          <div className="error">{formik.errors.email}</div>
        ) : null}
      </div>

      <div className="form-control">
        <label htmlFor="phoneNumber">Phone Number:</label>
        <input
          id="phoneNumber"
          name="phoneNumber"
          type="text"
          onChange={formik.handleChange}
          onBlur={formik.handleBlur}
          value={formik.values.phoneNumber}
        />
        {formik.touched.phoneNumber && formik.errors.phoneNumber ? (
          <div className="error">{formik.errors.phoneNumber}</div>
        ) : null}
      </div>

      <div className="form-control">
        <label htmlFor="applyingFor">Applying for Position:</label>
        <select
          id="applyingFor"
          name="applyingFor"
          onChange={handleApplyingForChange} // Changed to use separate handler
          onBlur={formik.handleBlur}
          value={formik.values.applyingFor}
        >
          <option value="" label="Select position" />
          <option value="Developer" label="Developer" />
          <option value="Designer" label="Designer" />
          <option value="Manager" label="Manager" />
        </select>
        {formik.touched.applyingFor && formik.errors.applyingFor ? (
          <div className="error">{formik.errors.applyingFor}</div>
        ) : null}
      </div>

      {(position === 'Developer' || position === 'Designer') && (
        <div className="form-control">
          <label htmlFor="relevantExperience">Relevant Experience (years):</label>
          <input
            id="relevantExperience"
            name="relevantExperience"
            type="text"
            onChange={formik.handleChange}
            onBlur={formik.handleBlur}
            value={formik.values.relevantExperience}
          />
          {formik.touched.relevantExperience && formik.errors.relevantExperience ? (
            <div className="error">{formik.errors.relevantExperience}</div>
          ) : null}
        </div>
      )}

      {position === 'Designer' && (
        <div className="form-control">
          <label htmlFor="portfolioURL">Portfolio URL:</label>
          <input
            id="portfolioURL"
            name="portfolioURL"
            type="text"
            onChange={formik.handleChange}
            onBlur={formik.handleBlur}
            value={formik.values.portfolioURL}
          />
          {formik.touched.portfolioURL && formik.errors.portfolioURL ? (
            <div className="error">{formik.errors.portfolioURL}</div>
          ) : null}
        </div>
      )}

      {position === 'Manager' && (
        <div className="form-control">
          <label htmlFor="managementExperience">Management Experience:</label>
          <input
            id="managementExperience"
            name="managementExperience"
            type="text"
            onChange={formik.handleChange}
            onBlur={formik.handleBlur}
            value={formik.values.managementExperience}
          />
          {formik.touched.managementExperience && formik.errors.managementExperience ? (
            <div className="error">{formik.errors.managementExperience}</div>
          ) : null}
        </div>
      )}

      <div className="form-control">
        <label>Additional Skills:</label>
        <div>
          <label>
            <input
              name="additionalSkills"
              type="checkbox"
              value="JavaScript"
              onChange={formik.handleChange}
              onBlur={formik.handleBlur}
              checked={formik.values.additionalSkills.includes('JavaScript')}
            />
            JavaScript
          </label>
          <label>
            <input
              name="additionalSkills"
              type="checkbox"
              value="CSS"
              onChange={formik.handleChange}
              onBlur={formik.handleBlur}
              checked={formik.values.additionalSkills.includes('CSS')}
            />
            CSS
          </label>
          <label>
            <input
              name="additionalSkills"
              type="checkbox"
              value="Python"
              onChange={formik.handleChange}
              onBlur={formik.handleBlur}
              checked={formik.values.additionalSkills.includes('Python')}
            />
            Python
          </label>
        </div>
        {formik.touched.additionalSkills && formik.errors.additionalSkills ? (
          <div className="error">{formik.errors.additionalSkills}</div>
        ) : null}
      </div>

      <div className="form-control">
        <label htmlFor="preferredInterviewTime">Preferred Interview Time:</label>
        <input
          id="preferredInterviewTime"
          name="preferredInterviewTime"
          type="datetime-local"
          onChange={formik.handleChange}
          onBlur={formik.handleBlur}
          value={formik.values.preferredInterviewTime}
        />
        {formik.touched.preferredInterviewTime && formik.errors.preferredInterviewTime ? (
          <div className="error">{formik.errors.preferredInterviewTime}</div>
        ) : null}
      </div>

      <button type="submit">Submit</button>
    </form>
  );
};

export default JobApplicationForm;
