import React, { useState } from 'react';
import { useFormik } from 'formik';
import * as Yup from 'yup';

const JobApplicationForm = () => {
  const [position, setPosition] = useState('');

  const formik = useFormik({
    initialValues: {
      fullName: '',
      email: '',
      phoneNumber: '',
      applyingFor: '',
      relevantExperience: '',
      portfolioURL: '',
      managementExperience: '',
      additionalSkills: [],
      preferredInterviewTime: '',
    },
    validationSchema: Yup.object({
      fullName: Yup.string().required('Full Name is required'),
      email: Yup.string().email('Invalid email address').required('Email is required'),
      phoneNumber: Yup.number().typeError('Must be a valid number').required('Phone Number is required'),
      relevantExperience: Yup.number().when('applyingFor', {
        is: (val) => val === 'Developer' || val === 'Designer',
        then: Yup.number().typeError('Must be a valid number').positive('Must be greater than 0').required('Relevant Experience is required')
      }),
      portfolioURL: Yup.string().when('applyingFor', {
        is: 'Designer',
        then: Yup.string().url('Invalid URL').required('Portfolio URL is required')
      }),
      managementExperience: Yup.string().when('applyingFor', {
        is: 'Manager',
        then: Yup.string().required('Management Experience is required')
      }),
      additionalSkills: Yup.array().min(1, 'At least one skill must be selected'),
      preferredInterviewTime: Yup.date().required('Preferred Interview Time is required').nullable(),
    }),
    onSubmit: values => {
      alert(JSON.stringify(values, null, 2));
    },
  });

  return (
    <form onSubmit={formik.handleSubmit}>
      <div>
        <label htmlFor="fullName">Full Name:</label>
        <input
          id="fullName"
          name="fullName"
          type="text"
          onChange={formik.handleChange}
          onBlur={formik.handleBlur}
          value={formik.values.fullName}
        />
        {formik.touched.fullName && formik.errors.fullName ? <div>{formik.errors.fullName}</div> : null}
      </div>

      <div>
        <label htmlFor="email">Email:</label>
        <input
          id="email"
          name="email"
          type="email"
          onChange={formik.handleChange}
          onBlur={formik.handleBlur}
          value={formik.values.email}
        />
        {formik.touched.email && formik.errors.email ? <div>{formik.errors.email}</div> : null}
      </div>

      <div>
        <label htmlFor="phoneNumber">Phone Number:</label>
        <input
          id="phoneNumber"
          name="phoneNumber"
          type="text"
          onChange={formik.handleChange}
          onBlur={formik.handleBlur}
          value={formik.values.phoneNumber}
        />
        {formik.touched.phoneNumber && formik.errors.phoneNumber ? <div>{formik.errors.phoneNumber}</div> : null}
      </div>

      <div>
        <label htmlFor="applyingFor">Applying for Position:</label>
        <select
          id="applyingFor"
          name="applyingFor"
          onChange={(e) => {
            formik.handleChange(e);
            setPosition(e.target.value);
          }}
          onBlur={formik.handleBlur}
          value={formik.values.applyingFor}
        >
          <option value="" label="Select position" />
          <option value="Developer" label="Developer" />
          <option value="Designer" label="Designer" />
          <option value="Manager" label="Manager" />
        </select>
        {formik.touched.applyingFor && formik.errors.applyingFor ? <div>{formik.errors.applyingFor}</div> : null}
      </div>

      {(position === 'Developer' || position === 'Designer') && (
        <div>
          <label htmlFor="relevantExperience">Relevant Experience (years):</label>
          <input
            id="relevantExperience"
            name="relevantExperience"
            type="text"
            onChange={formik.handleChange}
            onBlur={formik.handleBlur}
            value={formik.values.relevantExperience}
          />
          {formik.touched.relevantExperience && formik.errors.relevantExperience ? <div>{formik.errors.relevantExperience}</div> : null}
        </div>
      )}

      {position === 'Designer' && (
        <div>
          <label htmlFor="portfolioURL">Portfolio URL:</label>
          <input
            id="portfolioURL"
            name="portfolioURL"
            type="text"
            onChange={formik.handleChange}
            onBlur={formik.handleBlur}
            value={formik.values.portfolioURL}
          />
          {formik.touched.portfolioURL && formik.errors.portfolioURL ? <div>{formik.errors.portfolioURL}</div> : null}
        </div>
      )}

      {position === 'Manager' && (
        <div>
          <label htmlFor="managementExperience">Management Experience:</label>
          <input
            id="managementExperience"
            name="managementExperience"
            type="text"
            onChange={formik.handleChange}
            onBlur={formik.handleBlur}
            value={formik.values.managementExperience}
          />
          {formik.touched.managementExperience && formik.errors.managementExperience ? <div>{formik.errors.managementExperience}</div> : null}
        </div>
      )}

      <div>
        <label>Additional Skills:</label>
        <div>
          <label>
            <input
              name="additionalSkills"
              type="checkbox"
              value="JavaScript"
              onChange={formik.handleChange}
              onBlur={formik.handleBlur}
              checked={formik.values.additionalSkills.includes('JavaScript')}
            />
            JavaScript
          </label>
          <label>
            <input
              name="additionalSkills"
              type="checkbox"
              value="CSS"
              onChange={formik.handleChange}
              onBlur={formik.handleBlur}
              checked={formik.values.additionalSkills.includes('CSS')}
            />
            CSS
          </label>
          <label>
            <input
              name="additionalSkills"
              type="checkbox"
              value="Python"
              onChange={formik.handleChange}
              onBlur={formik.handleBlur}
              checked={formik.values.additionalSkills.includes('Python')}
            />
            Python
          </label>
        </div>
        {formik.touched.additionalSkills && formik.errors.additionalSkills ? <div>{formik.errors.additionalSkills}</div> : null}
      </div>

      <div>
        <label htmlFor="preferredInterviewTime">Preferred Interview Time:</label>
        <input
          id="preferredInterviewTime"
          name="preferredInterviewTime"
          type="datetime-local"
          onChange={formik.handleChange}
          onBlur={formik.handleBlur}
          value={formik.values.preferredInterviewTime}
        />
        {formik.touched.preferredInterviewTime && formik.errors.preferredInterviewTime ? <div>{formik.errors.preferredInterviewTime}</div> : null}
      </div>

      <button type="submit">Submit</button>
    </form>
  );
};

export default JobApplicationForm;
