import React, { useState } from 'react';
import { Formik } from 'formik';
import './styles.css'; // Import your CSS file for styling

const JobApplicationForm = () => {
  // State to manage form fields and validation
  const [formData, setFormData] = useState({
    fullName: '',
    email: '',
    phoneNumber: '',
    applyingFor: '',
    relevantExperience: '',
    portfolioURL: '',
    managementExperience: '',
    additionalSkills: [],
    preferredInterviewTime: '',
  });

  const [formErrors, setFormErrors] = useState({});

  // Handle form field changes
  const handleInputChange = (e) => {
    const { name, value, type, checked } = e.target;
    const fieldValue = type === 'checkbox' ? (checked ? [...formData[name], value] : formData[name].filter(item => item !== value)) : value;

    setFormData({
      ...formData,
      [name]: fieldValue,
    });
  };

  // Handle form submission
  const handleSubmit = (e) => {
    e.preventDefault();
    const errors = validateForm(formData);
    if (Object.keys(errors).length === 0) {
      alert('Form submitted successfully!');
      // Handle form submission logic here (e.g., API call)
    } else {
      setFormErrors(errors);
    }
  };

  // Custom validation function
  const validateForm = (data) => {
    let errors = {};

    // Validate each field manually
    if (!data.fullName.trim()) {
      errors.fullName = 'Full Name is required';
    }

    if (!data.email.trim()) {
      errors.email = 'Email is required';
    } else if (!/\S+@\S+\.\S+/.test(data.email)) {
      errors.email = 'Invalid email address';
    }

    if (!data.phoneNumber.trim()) {
      errors.phoneNumber = 'Phone Number is required';
    } else if (!/^\d+$/.test(data.phoneNumber)) {
      errors.phoneNumber = 'Phone Number must be a valid number';
    }

    if (!data.applyingFor) {
      errors.applyingFor = 'Applying for Position is required';
    }

    if ((data.applyingFor === 'Developer' || data.applyingFor === 'Designer') && !data.relevantExperience) {
      errors.relevantExperience = 'Relevant Experience is required';
    } else if ((data.applyingFor === 'Developer' || data.applyingFor === 'Designer') && isNaN(Number(data.relevantExperience))) {
      errors.relevantExperience = 'Relevant Experience must be a number';
    } else if ((data.applyingFor === 'Developer' || data.applyingFor === 'Designer') && Number(data.relevantExperience) <= 0) {
      errors.relevantExperience = 'Relevant Experience must be greater than 0';
    }

    if (data.applyingFor === 'Designer' && !data.portfolioURL.trim()) {
      errors.portfolioURL = 'Portfolio URL is required';
    } else if (data.applyingFor === 'Designer' && !isValidUrl(data.portfolioURL)) {
      errors.portfolioURL = 'Invalid URL';
    }

    if (data.applyingFor === 'Manager' && !data.managementExperience.trim()) {
      errors.managementExperience = 'Management Experience is required';
    }

    if (data.additionalSkills.length === 0) {
      errors.additionalSkills = 'At least one skill must be selected';
    }

    if (!data.preferredInterviewTime.trim()) {
      errors.preferredInterviewTime = 'Preferred Interview Time is required';
    }

    return errors;
  };

  // Helper function to validate URL
  const isValidUrl = (url) => {
    try {
      new URL(url);
      return true;
    } catch (error) {
      return false;
    }
  };

  return (
    <form onSubmit={formik.handleSubmit} className="job-application-form">
      <div className="form-control">
        <label htmlFor="fullName">Full Name:</label>
        <input
          id="fullName"
          name="fullName"
          type="text"
          onChange={formik.handleChange}
          onBlur={formik.handleBlur}
          value={formik.values.fullName}
        />
        {formik.touched.fullName && formik.errors.fullName ? <div className="error">{formik.errors.fullName}</div> : null}
      </div>

      <div className="form-control">
        <label htmlFor="email">Email:</label>
        <input
          id="email"
          name="email"
          type="email"
          onChange={formik.handleChange}
          onBlur={formik.handleBlur}
          value={formik.values.email}
        />
        {formik.touched.email && formik.errors.email ? <div className="error">{formik.errors.email}</div> : null}
      </div>

      <div className="form-control">
        <label htmlFor="phoneNumber">Phone Number:</label>
        <input
          id="phoneNumber"
          name="phoneNumber"
          type="text"
          onChange={formik.handleChange}
          onBlur={formik.handleBlur}
          value={formik.values.phoneNumber}
        />
        {formik.touched.phoneNumber && formik.errors.phoneNumber ? <div className="error">{formik.errors.phoneNumber}</div> : null}
      </div>

      <div className="form-control">
        <label htmlFor="applyingFor">Applying for Position:</label>
        <select
          id="applyingFor"
          name="applyingFor"
          onChange={(e) => {
            formik.handleChange(e);
            setPosition(e.target.value);
          }}
          onBlur={formik.handleBlur}
          value={formik.values.applyingFor}
        >
          <option value="" label="Select position" />
          <option value="Developer" label="Developer" />
          <option value="Designer" label="Designer" />
          <option value="Manager" label="Manager" />
        </select>
        {formik.touched.applyingFor && formik.errors.applyingFor ? <div className="error">{formik.errors.applyingFor}</div> : null}
      </div>

      {(position === 'Developer' || position === 'Designer') && (
        <div className="form-control">
          <label htmlFor="relevantExperience">Relevant Experience (years):</label>
          <input
            id="relevantExperience"
            name="relevantExperience"
            type="text"
            onChange={formik.handleChange}
            onBlur={formik.handleBlur}
            value={formik.values.relevantExperience}
          />
          {formik.touched.relevantExperience && formik.errors.relevantExperience ? <div className="error">{formik.errors.relevantExperience}</div> : null}
        </div>
      )}

      {position === 'Designer' && (
        <div className="form-control">
          <label htmlFor="portfolioURL">Portfolio URL:</label>
          <input
            id="portfolioURL"
            name="portfolioURL"
            type="text"
            onChange={formik.handleChange}
            onBlur={formik.handleBlur}
            value={formik.values.portfolioURL}
          />
          {formik.touched.portfolioURL && formik.errors.portfolioURL ? <div className="error">{formik.errors.portfolioURL}</div> : null}
        </div>
      )}

      {position === 'Manager' && (
        <div className="form-control">
          <label htmlFor="managementExperience">Management Experience:</label>
          <input
            id="managementExperience"
            name="managementExperience"
            type="text"
            onChange={formik.handleChange}
            onBlur={formik.handleBlur}
            value={formik.values.managementExperience}
          />
          {formik.touched.managementExperience && formik.errors.managementExperience ? <div className="error">{formik.errors.managementExperience}</div> : null}
        </div>
      )}

      <div className="form-control">
        <label>Additional Skills:</label>
        <div>
          <label>
            <input
              name="additionalSkills"
              type="checkbox"
              value="JavaScript"
              onChange={formik.handleChange}
              onBlur={formik.handleBlur}
              checked={formik.values.additionalSkills.includes('JavaScript')}
            />
            JavaScript
          </label>
          <label>
            <input
              name="additionalSkills"
              type="checkbox"
              value="CSS"
              onChange={formik.handleChange}
              onBlur={formik.handleBlur}
              checked={formik.values.additionalSkills.includes('CSS')}
            />
            CSS
          </label>
          <label>
            <input
              name="additionalSkills"
              type="checkbox"
              value="Python"
              onChange={formik.handleChange}
              onBlur={formik.handleBlur}
              checked={formik.values.additionalSkills.includes('Python')}
            />
            Python
          </label>
        </div>
        {formik.touched.additionalSkills && formik.errors.additionalSkills ? <div className="error">{formik.errors.additionalSkills}</div> : null}
      </div>

      <div className="form-control">
        <label htmlFor="preferredInterviewTime">Preferred Interview Time:</label>
        <input
          id="preferredInterviewTime"
          name="preferredInterviewTime"
          type="datetime-local"
          onChange={formik.handleChange}
          onBlur={formik.handleBlur}
          value={formik.values.preferredInterviewTime}
        />
        {formik.touched.preferredInterviewTime && formik.errors.preferredInterviewTime ? <div className="error">{formik.errors.preferredInterviewTime}</div> : null}
      </div>

      <button type="submit">Submit</button>
    </form>
  );
};

export default JobApplicationForm;
